import { useRef, useState } from 'react';
import classes from './Checkout.module.css';

const isEmpty = value => value.trim() === '';
const isFiveChars = value => value.trim().length === 5;
const Checkout = props => {
  const [formValidation, setFormValidation] = useState();
  const nameInputRef = useRef();
  const streetInputRef = useRef();
  const postalInputRef = useRef();
  const cityInputRef = useRef();

  const confirmHandler = event => {
    event.preventDefault();
    const enteredName = nameInputRef.current.value;
    const enteredStreet = nameInputRef.current.value;
    const enteredPostal = nameInputRef.current.value;
    const enteredCity = nameInputRef.current.value;

    const enteredNameIsValid = !isEmpty(nameInputRef.current.value);
    const enteredStreetIsValid = !isEmpty(streetInputRef.current.value);
    const enteredPostalIsValid = isFiveChars(postalInputRef.current.value);
    const enteredCityIsValid = !isEmpty(cityInputRef.current.value);

    setFormValidation(
      enteredNameIsValid &&
        enteredStreetIsValid &&
        enteredPostalIsValid &&
        enteredCityIsValid
    );
  };
  const onChangeNameHandler = e => {
    isEmpty(e.target.value);
  };
  const onChangeStreetHandler = e => {
    isEmpty(e.target.value);
  };
  const onChangePostalHandler = e => {
    isEmpty(e.target.value);
  };
  const onChangeCityHandler = e => {
    isEmpty(e.target.value);
  };

  return (
    <form className={classes.form} onSubmit={confirmHandler}>
      <div
        className={`${classes.control} ${
          !formValidation ? classes.invalid : ''
        }`}
      >
        <label htmlFor='name'>Your Name</label>
        <input
          ref={nameInputRef}
          onChange={onChangeNameHandler}
          type='text'
          id='name'
        />
      </div>
      <div
        className={`${classes.control} ${
          !formValidation ? classes.invalid : ''
        }`}
      >
        <label htmlFor='street'>Street</label>
        <input
          ref={streetInputRef}
          onChange={onChangeStreetHandler}
          type='text'
          id='street'
        />
      </div>
      <div
        className={`${classes.control} ${
          !formValidation ? classes.invalid : ''
        }`}
      >
        <label htmlFor='postal'>Postal Code</label>
        <input
          ref={postalInputRef}
          onChange={onChangePostalHandler}
          type='text'
          id='postal'
        />
      </div>
      <div
        className={`${classes.control} ${
          !formValidation ? classes.invalid : ''
        }`}
      >
        <label htmlFor='city'>City</label>
        <input
          ref={cityInputRef}
          onChange={onChangeCityHandler}
          type='text'
          id='city'
        />
      </div>
      <div className={classes.actions}>
        <button type='button' onClick={props.onCancelCheckout}>
          Cancel
        </button>
        <button className={classes.submit}>Confirm</button>
      </div>
    </form>
  );
};

export default Checkout;
